  EObject eObj = ctx.getTarget();
  EMFEventType eType = ctx.getEventType();
  if (eType == EMFEventType.NULL && useleseInstanceOfCheck(MyType.class, eObj) {
    MyType t = (MyType) eObj;
    return validateMyType((MyType)eObj, ctx) // now finally start validating something
  }
}

